package LogicalCode;

public class factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=5;
		int factorial=1;
		for(int i=1; i<=5;i++) {
			factorial*=i;
			
			
		}
		System.out.println("the factorial of " +num+ " is :"+factorial);

	}

}
